//——————————————————————————————————————————————————————————————————————————————————————————————————————————————————————
//   EBSelection
//——————————————————————————————————————————————————————————————————————————————————————————————————————————————————————

enum EBSelection <T> : Equatable where T : Equatable {

  static func == (lhs : EBSelection <T>, rhs : EBSelection <T>) -> Bool {
    switch (lhs, rhs) {
    case (.empty, .empty) :
      return true
    case (.multiple, .multiple) :
      return true
    case (.single (let left), .single (let right)) :
      return left == right
    default :
      return false
    }
  }

  case empty
  case multiple
  case single (T)

  func kind () -> EBPropertyKind {
    switch self {
    case .empty : return .noSelectionKind
    case .multiple : return .multipleSelectionKind
    case .single : return .singleSelectionKind
    }
  }
}

//——————————————————————————————————————————————————————————————————————————————————————————————————————————————————————

enum EBPropertyKind {
  case noSelectionKind
  case multipleSelectionKind
  case singleSelectionKind
}

//——————————————————————————————————————————————————————————————————————————————————————————————————————————————————————

func &= (left : inout EBPropertyKind, right : EBPropertyKind) {
  switch left {
  case .noSelectionKind : break
  case .multipleSelectionKind :
    if right == .noSelectionKind {
      left = .noSelectionKind
    }
  case .singleSelectionKind :
    switch right {
    case .noSelectionKind :
      left = .noSelectionKind
    case .multipleSelectionKind :
      left = .multipleSelectionKind
    case .singleSelectionKind :
      break
    }
  }
}

//——————————————————————————————————————————————————————————————————————————————————————————————————————————————————————

fileprivate func compareIntProperties (left : EBSelection <Int>,
                                       right : EBSelection <Int>,
                                       function : (Int, Int) -> Bool) -> EBSelection <Bool> {
  switch left {
  case .empty :
    return .empty
  case .multiple :
    switch right {
    case .empty :
      return .empty
    case .multiple, .single :
      return .multiple
    }
  case .single (let vg) :
    switch right {
    case .empty :
      return .empty
    case .multiple :
      return .multiple
    case .single (let vd) :
      return .single (function (vg, vd))
    }
  }
}

//——————————————————————————————————————————————————————————————————————————————————————————————————————————————————————

func > (left : EBSelection<Int>, right : EBSelection<Int>) -> EBSelection<Bool> {
  return compareIntProperties (left: left, right: right) {$0 > $1}
}

//——————————————————————————————————————————————————————————————————————————————————————————————————————————————————————

func >= (left : EBSelection<Int>, right : EBSelection<Int>) -> EBSelection<Bool> {
  return compareIntProperties (left: left, right: right) {$0 >= $1}
}

//——————————————————————————————————————————————————————————————————————————————————————————————————————————————————————

func < (left : EBSelection<Int>, right : EBSelection<Int>) -> EBSelection<Bool> {
  return compareIntProperties (left: left, right: right) {$0 < $1}
}

//——————————————————————————————————————————————————————————————————————————————————————————————————————————————————————

func <= (left : EBSelection<Int>, right : EBSelection<Int>) -> EBSelection<Bool> {
  return compareIntProperties (left: left, right: right) {$0 <= $1}
}

//——————————————————————————————————————————————————————————————————————————————————————————————————————————————————————

func == (left:EBSelection<Int>, right:EBSelection<Int>) -> EBSelection<Bool> {
  return compareIntProperties (left: left, right: right) {$0 == $1}
}

//——————————————————————————————————————————————————————————————————————————————————————————————————————————————————————

func != (left : EBSelection<Int>, right : EBSelection<Int>) -> EBSelection<Bool> {
  return compareIntProperties (left: left, right: right) {$0 != $1}
}

//——————————————————————————————————————————————————————————————————————————————————————————————————————————————————————

fileprivate func combineBoolProperties (left : EBSelection<Bool>,
                                        right : EBSelection<Bool>,
                                        function : (Bool, Bool) -> Bool) -> EBSelection<Bool> {
  switch left {
  case .empty :
    return .empty
  case .multiple :
    switch right {
    case .empty :
      return .empty
    case .multiple, .single :
      return .multiple
    }
  case .single (let vg) :
    switch right {
    case .empty :
      return .empty
    case .multiple :
      return .multiple
    case .single (let vd) :
      return .single (function (vg, vd))
    }
  }
}

//——————————————————————————————————————————————————————————————————————————————————————————————————————————————————————

func && (left : EBSelection<Bool>, right : EBSelection<Bool>) -> EBSelection<Bool> {
  return combineBoolProperties (left: left, right: right, function: {$0 && $1})
}

//——————————————————————————————————————————————————————————————————————————————————————————————————————————————————————

func || (left : EBSelection<Bool>, right : EBSelection<Bool>) -> EBSelection<Bool> {
  return combineBoolProperties (left: left, right: right, function: {$0 || $1})
}

//——————————————————————————————————————————————————————————————————————————————————————————————————————————————————————

func ^ (left : EBSelection<Bool>, right : EBSelection<Bool>) -> EBSelection<Bool> {
  return combineBoolProperties (left: left, right: right, function: {$0 != $1})
}

//——————————————————————————————————————————————————————————————————————————————————————————————————————————————————————

prefix func ! (operand : EBSelection<Bool>) -> EBSelection<Bool> {
  switch operand {
  case .empty :
    return .empty
  case .multiple :
    return .multiple
  case .single (let v) :
    return .single (!v)
  }
}

//——————————————————————————————————————————————————————————————————————————————————————————————————————————————————————
//    ReadOnlyAbstractArrayProperty
//——————————————————————————————————————————————————————————————————————————————————————————————————————————————————————

class ReadOnlyAbstractArrayProperty <T : Hashable> : EBAbstractProperty {

  //····················································································································

  var prop : EBSelection < [T] > { get { return .empty } }

  //····················································································································

  weak var ebUndoManager : EBUndoManager? = nil // SOULD BE WEAK

  //····················································································································

  var propval : [T] { return [] } // Abstract method

  //····················································································································

  var propset : Set <T> { return Set () } // Abstract method (requires T to be hashable)

  //····················································································································

  final var count_property = EBTransientProperty_Int ()

  var count_property_selection : EBSelection <Int> {
    get {
      return self.count_property.prop
    }
  }

  //····················································································································

  override init () {
    super.init ()
    self.count_property.mReadModelFunction = { [weak self] in
      if let me = self {
        switch me.prop {
        case .empty :
          return .empty
        case .multiple :
          return .multiple
        case .single (let v) :
          return .single (v.count)
        }
      }else{
        return .empty
      }
    }
  }

  //····················································································································

  override func postEvent () {
    self.count_property.postEvent ()
    super.postEvent ()
  }

  //····················································································································

}

//——————————————————————————————————————————————————————————————————————————————————————————————————————————————————————
//    Data extension: comparable
//——————————————————————————————————————————————————————————————————————————————————————————————————————————————————————

extension Data : Comparable {

  //····················································································································

  static public func < (_ left : Data, _ right : Data) -> Bool {
    var result = left.count < right.count
    if left.count == right.count {
      var idx = 0
      while (idx < right.count) && !result {
        result = left [idx] < right [idx]
        idx += 1
      }
    }
    return result
  }

  //····················································································································

}

