import Cocoa

//----------------------------------------------------------------------------*

extension PMPrefs {
  func validate_mIntegerValue (proposedValue : Int) -> PMValidationResult {
//--- START OF USER ZONE 2
    var result = PMValidationResult.ok
    let validates = (proposedValue & 1) == 0
    if !validates {
      result = PMValidationResult.rejectWithAlert ("Even values are required.")
    }
    return result
//--- END OF USER ZONE 2

  }
}

