import Cocoa

//---------------------------------------------------------------------------------------------------------------------*

extension Preferences {
  func validate_mIntegerValue (currentValue : Int,
                               proposedValue : Int) -> EBValidationResult <Int> {
//--- START OF USER ZONE 2
    let result : EBValidationResult <Int>
    let validates = (proposedValue & 1) == 0 // Even value
    if validates {
      result = EBValidationResult.ok (proposedValue)
    }else{
      result = EBValidationResult.rejectWithAlert ("An even value is required here")
    }
    return result
//--- END OF USER ZONE 2
  }
}

