let TRANSIENT_COUNT = 0

//-----------------------------------------------------------------------------*

func enterTriggerForObject (inObject:AnyObject, inIndex:Int) {
  var theApp = NSApp as PMApplication
  theApp.enterTriggerForObject (inObject, inIndex:inIndex) ;
}

