//-----------------------------------------------------------------------------*

@objc(PMApplication) class PMApplication : NSApplication {

  //---------------------------------------------------------------------------*

  var mDeferredMessageSent = false
  var mObserverObjectArray : NSMutableSet[] = []
  var mTriggeredPropertyArray : String [] = []

  //---------------------------------------------------------------------------*

  init () {
    super.init ()
    for i in 0..TRANSIENT_COUNT {
      mObserverObjectArray += NSMutableSet ()
    }
  }

  //---------------------------------------------------------------------------*
  
  func enterTriggerForObject (inObject : AnyObject, inIndex: Int) {
    var aSet = mObserverObjectArray [inIndex]
    aSet.addObject (inObject) ;
    if !mDeferredMessageSent {
      mDeferredMessageSent = true ;
      var event = NSEvent.otherEventWithType (NSApplicationDefined,
        location:NSPoint (x:0.0, y:0.0),
        modifierFlags:0, // NSEventModifierFlags (),
        timestamp:0.0,
        windowNumber:0,
        context:NSGraphicsContext.currentContext(),
        subtype:0,
        data1:0,
        data2:0
      ) ;
      self.postEvent (event, atStart:false)
    }
  }
  
  //---------------------------------------------------------------------------*
  
  override func sendEvent (inEvent : NSEvent!) {
    if inEvent.type () == NSApplicationDefined {
      self.triggerChange ()
    }else{
      super.sendEvent (inEvent)
    }
  }
  
  //---------------------------------------------------------------------------*
  
  func triggerChange () {
    for i in 0..TRANSIENT_COUNT {
      var s = mObserverObjectArray [i]
      mObserverObjectArray [i] = NSMutableSet ()
      for object:AnyObject in s {
        object.willChangeValueForKey ("azerty")
        object.didChangeValueForKey  ("azerty")
 //       object.didChangeValueForKey  ([mTriggeredPropertyArray [i])
      }
    }
    mDeferredMessageSent = false ;
  }

  //---------------------------------------------------------------------------*

}

