func compute_PMDocument_countItemMessage (mNames : Int) -> String  {

//--- START OF USER ZONE 2
  var result = "No Item"
  if mNames == 1 {
    result = "1 Item"
  }else if mNames > 1 {
    result = NSString (format:"%ld Items", mNames)
  }
  return result
//--- END OF USER ZONE 2

}

