extension PMDocument {
  func increment (inSender : AnyObject) {
//--- START OF USER ZONE 2
    for object in rootObject.mNames.propval {
      object.aValue.setProp (object.aValue.propval + 1)
    }
//--- END OF USER ZONE 2

  }
