func transient_PMDocument_selectionCountString (
       _ self_selController_sortedArray_count : Int
) -> String {
//--- START OF USER ZONE 2
  if self_selController_sortedArray_count == 0 {
    return "No value"
  }else if self_selController_sortedArray_count == 1 {
    return "1 value"
  }else{
    return String (format:"%d values", self_selController_sortedArray_count)
  }
//--- END OF USER ZONE 2
}

