//----------------------------------------------------------------------------*
//    windowControllerDidLoadNib                                              *
//----------------------------------------------------------------------------*

- (void) windowControllerDidLoadNib: (NSWindowController *) inWindowController { 
  #ifdef EASY_BINDINGS_DEBUG
    NSLog (@"%s", __PRETTY_FUNCTION__) ;
  #endif
  [super windowControllerDidLoadNib:inWindowController] ;
//--- Disable undo registrations
  NSManagedObjectContext * moc = [self managedObjectContext] ;
  [[moc undoManager] disableUndoRegistration];
//--- Root entity controller creation
  mRootObjectController = [NSObjectController new] ;
  [mRootObjectController setAutomaticallyPreparesContent:YES] ;
  [mRootObjectController setEditable:YES] ;
  [mRootObjectController setEntityName:@"MyRootEntity"] ;
  [mRootObjectController setManagedObjectContext:moc] ;
//--- Root Object
  [mRootObjectController fetchWithRequest:nil merge:NO error:nil] ;
  mRootObject = [mRootObjectController content] ;
  if (nil == mRootObject) {
    mRootObject = [MyRootEntityClass insertNewObjectIntoManagedObjectContext:moc] ;
    [mRootObjectController setContent:mRootObject] ;
    // NSLog (@"Root object creation") ;
  }
//--- Enable undo registrations
  [moc processPendingChanges] ;
  [[moc undoManager] enableUndoRegistration];
//--- Outlet myTextField
  if (nil == myTextField) {
    presentErrorWindow (__FILE__, __LINE__, @"the 'myTextField' outlet is nil") ;
  }
  [myTextField
    bind:@"value"
    toObject:mRootObject
    withKeyPath:@"myString"
    options:nil
  ] ;
//--- Outlet myTextMajField
  if (nil == myTextMajField) {
    presentErrorWindow (__FILE__, __LINE__, @"the 'myTextMajField' outlet is nil") ;
  }
  [myTextMajField
    bind:@"value"
    toObject:mRootObject
    withKeyPath:@"myStringMaj"
    options:nil
  ] ;
//--- Outlet myTextMinField
  if (nil == myTextMinField) {
    presentErrorWindow (__FILE__, __LINE__, @"the 'myTextMinField' outlet is nil") ;
  }
  [myTextMinField
    bind:@"value"
    toObject:mRootObject
    withKeyPath:@"myStringMin"
    options:nil
  ] ;
//--- Outlet myTextConcatField
  if (nil == myTextConcatField) {
    presentErrorWindow (__FILE__, __LINE__, @"the 'myTextConcatField' outlet is nil") ;
  }
  [myTextConcatField
    bind:@"value"
    toObject:mRootObject
    withKeyPath:@"myStringConcat"
    options:nil
  ] ;

//--- Install transients observers
  [self pmInstallObserversForTransients] ;
}

