NSString * compute_PMDocument_countItemMessage (
    const NSUInteger inMNames_count) {

//--- START OF USER ZONE 2
  NSString * result = @"No Item" ;
  if (inMNames_count == 1) {
    result = @"1 Item" ;
  }else if (inMNames_count > 1) {
    result = [NSString stringWithFormat:@"%ld Items", inMNames_count] ;
  }
  return result ;
//--- END OF USER ZONE 2

