//----------------------------------------------------------------------------*
//    Init                                                                    *
//----------------------------------------------------------------------------*

- (id) init {
  #ifdef EASY_BINDINGS_DEBUG
    NSLog (@"%s", __PRETTY_FUNCTION__) ;
  #endif
  self = [super init] ;
  if (self) {
    #ifdef PM_COCOA_DEBUG
      objectHasBeenAllocated (self) ;
    #endif
  }
  return self ;
}

//----------------------------------------------------------------------------*
//    windowNibName                                                           *
//----------------------------------------------------------------------------*

- (NSString *) windowNibName {
  return @"PMDocument" ;
}

//----------------------------------------------------------------------------*
//    managedObjectModel                                                      *
//----------------------------------------------------------------------------*

- (NSManagedObjectModel *) managedObjectModel {
  return managedObjectModel () ;
}

//----------------------------------------------------------------------------*
//    windowControllerDidLoadNib                                              *
//----------------------------------------------------------------------------*

- (void) windowControllerDidLoadNib: (NSWindowController *) inWindowController { 
  #ifdef EASY_BINDINGS_DEBUG
    NSLog (@"%s", __PRETTY_FUNCTION__) ;
  #endif
  [super windowControllerDidLoadNib:inWindowController] ;
//--- Disable undo registrations
  NSManagedObjectContext * moc = [self managedObjectContext] ;
  [[moc undoManager] disableUndoRegistration];
//--- Root entity controller creation
  mRootObjectController = [NSObjectController new] ;
  [mRootObjectController setAutomaticallyPreparesContent:YES] ;
  [mRootObjectController setEditable:YES] ;
  [mRootObjectController setEntityName:@"MyRootEntity"] ;
  [mRootObjectController setManagedObjectContext:moc] ;
//--- Root Object
  [mRootObjectController fetchWithRequest:nil merge:NO error:nil] ;
  mRootObject = [mRootObjectController content] ;
  if (nil == mRootObject) {
    mRootObject = [MyEntityClass insertNewObjectIntoManagedObjectContext:moc] ;
    [mRootObjectController setContent:mRootObject] ;
    // NSLog (@"Root object creation") ;
  }
//--- Enable undo registrations
  [moc processPendingChanges] ;
  [[moc undoManager] enableUndoRegistration];
//--- Outlet docStringTextField
  if (nil == docStringTextField) {
    presentErrorWindow (__FILE__, __LINE__, @"the 'docStringTextField' outlet is nil") ;
  }
  [docStringTextField
    bind:@"value"
    toObject:mRootObject
    withKeyPath:@"docString"
    options:nil
  ] ;
//--- Outlet prefStringTextField
  if (nil == prefStringTextField) {
    presentErrorWindow (__FILE__, __LINE__, @"the 'prefStringTextField' outlet is nil") ;
  }
  [prefStringTextField
    bind:@"value"
    toObject:g_PMPrefs
    withKeyPath:@"myPrefString"
    options:nil
  ] ;
//--- Outlet prefTransientStringTextField
  if (nil == prefTransientStringTextField) {
    presentErrorWindow (__FILE__, __LINE__, @"the 'prefTransientStringTextField' outlet is nil") ;
  }
  [prefTransientStringTextField
    bind:@"value"
    toObject:g_PMPrefs
    withKeyPath:@"prefTransientString"
    options:nil
  ] ;
//--- Outlet transientConcatStringTextField
  if (nil == transientConcatStringTextField) {
    presentErrorWindow (__FILE__, __LINE__, @"the 'transientConcatStringTextField' outlet is nil") ;
  }
  [transientConcatStringTextField
    bind:@"value"
    toObject:self
    withKeyPath:@"transientConcatString"
    options:nil
  ] ;

//--- Install transients observers
  [self pmInstallObserversForTransients] ;
}

//----------------------------------------------------------------------------*
//   T R A N S I E N T    I M P L E M E N T A T I O N                         *
//----------------------------------------------------------------------------*

- (void) pmInstallObserversForTransients {
  // NSLog (@"%s", __PRETTY_FUNCTION__) ;
  [g_PMPrefs
    addObserver:self
    forKeyPath:@"myPrefString"
    options:0
    context:NULL
  ] ;
  [g_PMPrefs
    addObserver:self
    forKeyPath:@"prefTransientString"
    options:0
    context:NULL
  ] ;
  [mRootObject
    addObserver:self
    forKeyPath:@"docString"
    options:0
    context:NULL
  ] ;
}

//----------------------------------------------------------------------------*

- (void) observeValueForKeyPath:(NSString *) inKeyPath
         ofObject:(id) inObject
         change:(NSDictionary *) inChange
         context:(void *) inContext {
  // NSLog (@"%s, key %@", __PRETTY_FUNCTION__, inKey) ;
  if (inObject == g_PMPrefs) {
    if ([inKeyPath isEqualToString:@"myPrefString"]) {
      [self pmNotifyChangeForTransientProperty:@"transientConcatString"] ;
    }
    if ([inKeyPath isEqualToString:@"prefTransientString"]) {
      [self pmNotifyChangeForTransientProperty:@"transientConcatString"] ;
    }
  }else if (inObject == mRootObject) {
    if ([inKeyPath isEqualToString:@"docString"]) {
      [self pmNotifyChangeForTransientProperty:@"transientConcatString"] ;
    }
  }
}

//----------------------------------------------------------------------------*

- (NSString *) transientConcatString {
  return compute_PMDocument_transientConcatString (mRootObject.docString, g_PMPrefs.myPrefString, g_PMPrefs.prefTransientString).copy ;
}

