- (void) applicationWillTerminateAction: (NSNotification *) inNotification {
  NSUserDefaults * ud = [NSUserDefaults standardUserDefaults] ;
  [ud setObject: (myPrefString) forKey:@"PMPrefs:myPrefString"] ;
}

