//----------------------------------------------------------------------------*
//    fetchRootObject                                                         *
//----------------------------------------------------------------------------*

- (void) fetchRootObject { 
  #ifdef EASY_BINDINGS_DEBUG
    NSLog (@"%s", __PRETTY_FUNCTION__) ;
  #endif
  NSManagedObjectContext * moc = self.managedObjectContext ;
  NSFetchRequest * fetch = [NSFetchRequest new] ;
  fetch.entity = [NSEntityDescription
    entityForName:@"MyRootEntity"
    inManagedObjectContext:moc
  ] ;
  NSArray * array = [moc
    executeFetchRequest:fetch
    error:nil
  ] ;
  if (array.count == 0) {
    mRootObject = [NSEntityDescription
      insertNewObjectForEntityForName:@"MyRootEntity"
      inManagedObjectContext:moc
    ] ;
    [self hookOfRootObjectDidCreate] ;
  }else{
    mRootObject = [array objectAtIndex:0] ;
  }
}

